import React, { Fragment } from 'react'
import GoogleIcon from '@mui/icons-material/Google';
import FacebookIcon from '@mui/icons-material/Facebook';
import InstagramIcon from '@mui/icons-material/Instagram';
import LinkedInIcon from '@mui/icons-material/LinkedIn';
import { Card, Button } from 'react-bootstrap';
import c from '../images/courses/c.jpg';
import cpp from '../images/courses/c++.jpg';
import pyth from '../images/courses/python.jpg';
import webdev from '../images/courses/web.jpg';
import auto from '../images/courses/auto.jpg';
import ed from '../images/courses/3d.jpg';
import solid from '../images/courses/solid.jpg';
import mat from '../images/courses/mat.jpg';
import robo from '../images/courses/robotic.jpg';
import Courses from '../data/Courses.json';
import '../css/Home.css';


function Home() {

  return (
    <Fragment>
          <section id='home' className='pd-y mg-t'>
        <div className='container'> 
        <div className="row">
            <div className="col-md-6 col-12 home-text">
                <h1>beta academy</h1>
                <p>training center that aims to develop student's skills in technical and non-technical in all engineering fields.</p>
                <div className='social-icons'>
                  <a href='https://www.betaacademy18@gmail.com' target="_blank">
                    <GoogleIcon className='icon'></GoogleIcon>+ </a>
                  <a href='https://www.facebook.com/betaacademy.b' target="_blank">
                    <FacebookIcon className='icon'></FacebookIcon> </a>
                  <a href='https://www.instagram.com/beta_academy18' target="_blank"> 
                  <InstagramIcon className='icon'></InstagramIcon> </a>
                  <a href='https://www.linkedin.com/company/beta-training-academy' target="_blank">
                    <LinkedInIcon className='icon'></LinkedInIcon> </a>
                </div>
            </div> {/* ./home-text */}
            <div className='col-md-5 col-12 home-img'></div> {/* ./img */}
        </div> {/* ./row */}
        </div> {/*./container */}
    </section>
    {/* ./home */}
    <div className="divider"><div className='layer'></div></div> {/* ./divider*/}
    <section className="courses pd-y mt-5">
      <h2>courses & workshops </h2>
      <div className="container">
        <div className='row'>
        {
                Courses.map(course =>{
                  return(
                    <div className="col-lg-4 col-md-6 col-12">
                    <div className="course-card">
                    <Card>
                    <Card.Img variant="top" src={course.image}></Card.Img>
                    <Card.Body>
                      <Card.Title>{course.title}</Card.Title>
                      <Button className='card-btn'>show details</Button>
                    </Card.Body>
              </Card>
                    </div>
                  </div>
                  )
                })
              }
        </div> {/* ./row*/ }
      </div>
    </section>
    {/* ./courses */}
    </Fragment>
  )
}

export default Home
