import React, { Fragment } from 'react'
import GoogleIcon from '@mui/icons-material/Google';
import FacebookIcon from '@mui/icons-material/Facebook';
import InstagramIcon from '@mui/icons-material/Instagram';
import LinkedInIcon from '@mui/icons-material/LinkedIn';
import WhatsAppIcon from '@mui/icons-material/WhatsApp';
import { Carousel, Card, Button } from 'react-bootstrap';
import '../css/Home.css'


function Home() {
  return (
    <Fragment>
          <section id='home' className='pd-y mg-t'>
        <div className='container'> 
        <div className="row">
            <div className="col-md-6 col-12 home-text">
                <h1>beta academy</h1>
                <p>training center that aims to develop student's skills in technical and non-technical in all engineering fields.</p>
                <div className='social-icons'>
                  <a href='#' className='g'> <GoogleIcon className='icon'></GoogleIcon>+ </a>
                  <a href='#' className='fb'> <FacebookIcon className='icon'></FacebookIcon> </a>
                  <a href='#' className='in'> <InstagramIcon className='icon'></InstagramIcon> </a>
                  <a href='#' className='ln'> <LinkedInIcon className='icon'></LinkedInIcon> </a>
                  <a href='#' className='wb'> <WhatsAppIcon className='icon'></WhatsAppIcon> </a>
                </div>
            </div> {/* ./home-text */}
            <div className='col-md-5 col-12 home-img'></div> {/* ./img */}
        </div> {/* ./row */}
        </div> {/*./container */}
    </section>
    {/* ./home */}
    <div className="divider"><div className='layer'></div></div> {/* ./divider*/}
    <section className="courses pd-y mt-5">
      <h2>courses & workshops </h2>
      <div className="container">
      <Carousel>
  <Carousel.Item>
  <Card style={{ width: '18rem' }}>
  <Card.Img variant="top" src="holder.js/100px180" />
  <Card.Body>
    <Card.Title>Card Title</Card.Title>
    <Card.Text>
      Some quick example text to build on the card title and make up the bulk of
      the card's content.
    </Card.Text>
    <Button variant="primary">Go somewhere</Button>
  </Card.Body>
</Card>
  </Carousel.Item>
</Carousel>
      </div>
    </section>
    {/* ./courses */}
    </Fragment>
  )
}

export default Home
